    .priv_systick = SysTick_Handler,                                                                                                                                                                                                                                                                                          
};                                                                                                                                                                                                                                                                                                                            
UVISOR_SET_PRIV_SYS_IRQ_HOOKS(&priv_sys_irq_hooks);                                                                                                                                                                                                                                                                           

/* Enable uVisor. */                                                                                                                                                                                                                                                                                                          
UVISOR_SET_MODE_ACL(UVISOR_ENABLED, g_main_acl);
