my int @a = ();
for (1..1_000_000) -> int $item {
    @a.unshift($item);
}
jnthn@lviv:~/dev/MoarVM$ time raku push.raku 

real	0m0.247s
user	0m0.228s
sys	0m0.060s
jnthn@lviv:~/dev/MoarVM$ time raku unshift.raku

real	0m0.288s
user	0m0.252s
sys	0m0.068s

