char *getdirname(char *name) {
  char *end = name + strlen(name) - 1;

  while (*end == '/' && end > name) end--;
  while (*end != '/' && end >= name) end--;
  while (*end == '/' && end > name) end--;

  return ++end == name ? xstrdup(".") : xstrndup(name, end - name);
}

