    if (iv_len >= sizeof(ivtmp)) {
        XMEMCPY(ivtmp, iv, sizeof(ivtmp));
    } else {
        if (iv_len > 0)
            XMEMCPY(ivtmp, iv, iv_len);
        XMEMSET(ivtmp + iv_len, 0, (sizeof(ivtmp)) - iv_len);
    }

