    if (awrite)
    {
        aflags |= GENERIC_WRITE;
    }
    if (acreate)
    {
        dispos = atrunc ? CREATE_ALWAYS : OPEN_ALWAYS;
    }
    else
    {
        dispos = atrunc ? TRUNCATE_EXISTING : OPEN_EXISTING;
    }

