};

DCMI_MDMA_Handle0.Init.SourceBurst  = MDMA_BURST_SIZE[0][0];
DCMI_MDMA_Handle0.Init.DestBurst      = MDMA_BURST_SIZE[0][1];
for (uint32_t i=7; i>0; i--) {
